mapper.select:
SELECT MAX(id)
FROM EMPLOYEE
WHERE SUBSTR(id, 3, 2) = #{yearSuffix}

mapper.update:
UPDATE employee SET
phone = #{phone},
extension = #{extension},
email = #{email, jdbcType=VARCHAR},
profile_url = #{profile_url, jdbcType=VARCHAR}
WHERE id = #{id}

UPDATE employee
SET password = #{password}
WHERE ID = #{id}
